DROP DATABASE IF EXISTS pokebarter;
/* Creo la base de datos */
CREATE DATABASE pokebarter;

USE pokebarter; 
/* Creo la tabla para almacenar users. */
CREATE TABLE user(
    username VARCHAR(30)  PRIMARY KEY,
    email VARCHAR(30) NOT NULL,
    nombre VARCHAR(20),
    apellido VARCHAR(30),
    telefono VARCHAR(15),
    contraseña VARCHAR(50) NOT NULL,
    datos_adicionales VARCHAR(200),
    img  VARCHAR(200)
);
/* Creo la tabla para almacenar pokémon. */
CREATE TABLE pokemon (
    id INT(6) UNSIGNED AUTO_INCREMENT PRIMARY KEY,
    pokemon VARCHAR(30) NOT NULL,
    movimiento_1 VARCHAR(50),
    movimiento_2 VARCHAR(50),
    movimiento_3 VARCHAR(50),
    movimiento_4 VARCHAR(50),
    habilidad VARCHAR(50),
    nivel INT(3),
    shiny BOOLEAN NOT NULL,
    user VARCHAR(30) NOT NULL,
    estado VARCHAR(1) NOT NULL,
    juego VARCHAR(30) NOT NULL,
    img  VARCHAR(200) NOT NULL,
    FOREIGN KEY (user) REFERENCES user(username)
);

